mapper.select:
select id
from sys_auth_actiontree
where id=#{0}

mapper.update:
update sys_auth_actiontree
set sort = #{sort, jdbcType=INTEGER}
where id = #{id, jdbcType=VARCHAR}

update sys_auth_actiontree
set sort = #{sort, jdbcType=VARCHAR}
where parent_id = #{parentId, jdbcType=VARCHAR} and sort = #{sortNow, jdbcType=VARCHAR}

update sys_auth_actiontree
set sort = #{sort}
where id = #{id}
